package variablePractice;

public class AddMulDivSub {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		float x=35;
		float y= 12;
		float z= 8;
	
		float addition;
		float substract;
		float multiply;
		float division;
		
		 addition =x+y+z;
		substract = x-y-z;	 
		multiply = x*y*z;
		division = x/z ;
				
		 
      System.out.println("x+y+z= "+addition+"\n");
      System.out.println("x*y*z= "+multiply+"\n");
		System.out.println("x/z= "+division+"\r");
		System.out.println("x-y-z= "+substract+"\r");

	}

}
